
public class Exam04이단부터구단 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i = 2 ; i<10 ; i++) {
			System.out.println("");
			System.out.println("==" + i +"단==");
			for (int b = 1; b<10;b++) {
				System.out.println(i + "*" + b + "=" + b*i);
			}
		}
	}

}
